import {Component, EventEmitter, Output, ViewChild} from '@angular/core';
import {PointService} from "../../service/point.service";
import {FormBuilder} from "@angular/forms";
import {PointRequestDto} from "../../dto/point-request-dto";
import {PointResponse} from "../../dto/point-response";
import { MultiSelectModule } from 'primeng/multiselect';
@Component({
  selector: 'app-point-form',
  templateUrl: './point-form.component.html',
  styleUrls: ['./point-form.component.css']
})
export class PointFormComponent {

  pointForm = this.formBuilder.group({
    x_value: '',
    y_value: '',
    r_value: ''
  });


  @Output() addEvent = new EventEmitter<PointResponse>();
  @Output() rChangeEvent = new EventEmitter<number>()
  errorMessage: string = "";


  x_values: number[] = [-5, -4, -3, -2, -1, 0, 1, 2, 3];
  y_values: number[] = [-3, -2, -1, 0, 1, 2, 3];
  r_values: number[] = [1, 1.5, 2, 2.5, 3];

  constructor(private pointService: PointService,
              private formBuilder: FormBuilder) {
  }

  onSubmit(): void {
    this.errorMessage = "";

    if (this.pointForm.value.x_value === '' || this.pointForm.value.x_value == null) {
      this.errorMessage = "X value is required";
    } else if (this.pointForm.value.y_value === '' || this.pointForm.value.y_value == null) {
      this.errorMessage = "Y value is required";
    } else if (this.pointForm.value.r_value === '' || this.pointForm.value.r_value == null) {
      this.errorMessage = "R value is required";
    } else {

      console.log('Form data: ', this.pointForm.value);

      let point = new PointRequestDto(
        +this.pointForm.value.x_value,
        +this.pointForm.value.y_value,
        +this.pointForm.value.r_value,
      );


      this.pointService.savePoint(point).subscribe(
        data => {
          console.log("New point " + data.currentTime);
          this.addEvent.emit(data);
        },

        error => {
          console.log(error);
        }
      );


    }

  }

  onChangeR(e: any):void{
    if (e.target.value != ""){
      console.log();
      this.rChangeEvent.emit(e.target.value.split(": ")[1]);
    }

  }

}
<div class="card bg-dark m-auto" style="max-width: 65%">
  <div class="card-header text-center">Add new point</div>
  <div class="card-body" style="background-color: #d5d5d5">
    <form [formGroup]="pointForm" (ngSubmit)="onSubmit()">

      <div class="form-group">
        <p-multiSelect id="x_value" formControlName="x_value" [options]="x_values" placeholder="Choose X value"
                         appendTo="body"></p-multiSelect>
      </div>

      <div class="form-group">
        <label for="y_value">Choose Y value</label>
        <p-slider id="y_value" formControlName="y_value" [min]="-3" [max]="5" [step]="1" vertical="true"
                  ></p-slider>
      </div>
      <br>
      <div class="form-group">
        <label for="r_value">
          <select id="r_value" formControlName="r_value" class="form-select form-select-sm " required (change)="onChangeR($event)">
            <option value="" disabled selected>Choose R value</option>
            <option *ngFor="let r of r_values" [ngValue]="r">
              {{ r }}
            </option>
          </select>
        </label>
      </div>
      <br>

      <button class="btn btn-primary" type="submit">
        Submit
      </button>

      <div *ngIf="errorMessage!=''" class="alert alert-danger" style="margin-top: 10px;">
        {{errorMessage}}
      </div>

    </form>
  </div>


</div>
