import {AfterViewInit, Component, Injectable, NgModule, OnInit, ViewChild} from '@angular/core';
import {MatPaginator} from '@angular/material/paginator';
import {MatSort} from '@angular/material/sort';
import {MatTable} from '@angular/material/table';
import {PointResponse} from "../../dto/point-response";
import {PointService} from "../../service/point.service";
import {BehaviorSubject} from "rxjs";
import { PointGraphComponent } from '../point-graph/point-graph.component';
@NgModule({
  declarations: [
    PointGraphComponent,
  ],
})
@Component({
  selector: 'app-point-table',
  templateUrl: './point-table.component.html',
  styleUrls: ['./point-table.component.css']
})

export class PointTableComponent implements OnInit {
  @ViewChild(MatTable) table!: MatTable<PointResponse>;
  @ViewChild(MatPaginator) paginator! : MatPaginator;

  displayedColumns = ['x', 'y', 'r', 'currentTime', 'executeTime', 'hit'];
  dataSource: PointResponse[] = [];

  constructor(private service: PointService, private pointGraphComponent: PointGraphComponent) {
    console.log("im here");
  }

  ngOnInit(): void {
    this.service.getPoints().subscribe(data => {
      console.log(data);
      this.dataSource = data;
    })
  }

  addPoint(point:PointResponse) {
    this.dataSource.push(point);
    this.table.renderRows();
  }

  clearPoints() {
    this.pointGraphComponent.refresh(0);
    this.service.clearPoints();
    this.dataSource = [];
    this.table.renderRows();
  }


}
