import {Component, OnInit} from '@angular/core';
import {TokenStorageService} from "../../auth/token-storage.service";
import {PointResponse} from "../../dto/point-response";
import {PointService} from "../../service/point.service";

@Component({
  selector: 'app-user',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
export class MainComponent implements OnInit {
  info: any;

  constructor(private token: TokenStorageService,
              private pointService: PointService)  { }

  ngOnInit() {
    this.info = {
      token: this.token.getToken(),
      username: this.token.getUsername()
    };

  }
}
<div *ngIf="info.token; else loggedOut">
  <div class="row">
    <div class="col">
      <app-point-graph class="col-sm-6" (addEvent)="tableComponent.addPoint($event)" #graphComponent ></app-point-graph>
      <app-point-form class="col-sm-6" (addEvent)="tableComponent.addPoint($event);graphComponent.addPoint($event)" (rChangeEvent)="graphComponent.refresh($event)"></app-point-form>
    </div>
    <app-point-table class="col-sm-6" #tableComponent (refreshEvent)="graphComponent.refresh(0)"></app-point-table>

  </div>


</div>

<ng-template #loggedOut>
  <h1 class="text-info text-center ">Please login.</h1>
</ng-template>
